// src/App.js
import React from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons'; // Importe o novo componente
import logo from './assets/jek_logo.png'; // Importe o logo

const App = () => {
    const handleAdd = () => {
        alert("Adicionar ação executada!");
        // Lógica para adicionar um item
    };

    const handleRemove = () => {
        alert("Remover ação executada!");
        // Lógica para remover um item
    };

    return (
        <div style={{ position: 'relative' }}>
            <img 
                src={logo} 
                alt="Logo da biblioteca" 
                style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px', // Posiciona o logo no canto superior esquerdo
                    width: '150px', // Tamanho do logo
                    height: 'auto'
                }}
            />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '170px', // Ajuste para deixar espaço para o logo
                paddingTop: '40px', // Ajuste para espaçar verticalmente
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '20px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons onAdd={handleAdd} onRemove={handleRemove} /> {/* Adicione o componente */}
            </div>
        </div>
    );
};

export default App;
