// src/AddRemoveButtons.js
import React from 'react';

const AddRemoveButtons = ({ onAdd, onRemove }) => {
    return (
        <div style={{ display: 'flex', alignItems: 'center', marginLeft: '20px' }}> {}
            <button 
                onClick={onAdd} 
                style={{
                    padding: '10px 40px',
                    marginRight: '20px',
                    backgroundColor: '#4CAF50', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Adicionar livro
            </button>
            <button 
                onClick={onRemove} 
                style={{
                    padding: '10px 40px',
                    backgroundColor: '#f44336', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Remover livro
            </button>
        </div>
    );
};

export default AddRemoveButtons;
