// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import JekLogo from './Logo.js';
import DisplayBooks from './DisplayBooks';
import FilterBar from './FilterBar'; // Certifique-se de importar o FilterBar

const App = () => {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/');
            if (!response.ok) throw new Error('Erro ao buscar os livros');
            const data = await response.json();
            const updatedBooks = data.map(book => ({
                ...book,
                image: book.image ? `http://localhost:8000/media/${book.image}` : null,
            }));
            setBooks(updatedBooks);
            setFilteredBooks(updatedBooks); // Inicialmente, todos os livros são exibidos
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    const handleFilter = (filterFunction) => {
        const newFilteredBooks = books.filter(filterFunction);
        setFilteredBooks(newFilteredBooks);
    };

    return (
        <div style={{ position: 'relative' }}>
            <JekLogo />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '40px' }}>jeKlibrary</h1>
                <SearchBar onSearch={handleS} />
                <AddRemoveButtons onBookAdded={fetchBooks} />
            </div>
            <FilterBar books={books} onFilter={handleFilter} /> {/* Adiciona o FilterBar */}
            <div style={{ marginTop: '40px', paddingLeft: '200px', paddingRight: '20px' }}>
                <DisplayBooks books={filteredBooks} /> {/* Passa os livros filtrados para DisplayBooks */}
            </div>
        </div>
    );
};

export default App;
