// src/AddRemoveButtons.js
import React, { useState } from 'react';

const AddRemoveButtons = ({ onBookAdded }) => { // Receber a função como prop
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newBook, setNewBook] = useState({
        title: '',
        author: '',
        genre: '',
        published_date: '',
        n_pages: '',
        image: null,
    });

    const handleAdd = () => {
        setIsModalOpen(true);
    };

    const handleConfirmAdd = async () => {
        const formData = new FormData();
        formData.append('title', newBook.title);
        formData.append('author', newBook.author);
        formData.append('genre', newBook.genre);
        formData.append('published_date', newBook.published_date);
        formData.append('n_pages', newBook.n_pages);
        if (newBook.image) {
            formData.append('image', newBook.image);
        }

        try {
            const response = await fetch('http://localhost:8000/add/', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                alert("Livro adicionado com sucesso!");
                setIsModalOpen(false);
                setNewBook({
                    title: '',
                    author: '',
                    genre: '',
                    published_date: '',
                    n_pages: '',
                    image: null,
                });
                onBookAdded(); // Chama a função para indicar que um livro foi adicionado
            } else {
                alert("Erro ao adicionar livro");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', marginLeft: '20px' }}>
            <button 
                onClick={handleAdd} 
                style={{
                    padding: '10px 40px',
                    marginRight: '20px',
                    backgroundColor: '#64b627', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Adicionar livro
            </button>
            {/* ... resto do código do modal ... */}
        </div>
    );
};

export default AddRemoveButtons;
