// src/FilterBar.js
import React, { useState, useEffect } from "react";

const FilterBar = ({ books, onFilter }) => { // Recebe a lista de livros e a função onFilter como props
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState("");

    // Extrai os gêneros disponíveis a partir da lista de livros
    useEffect(() => {
        const uniqueGenres = Array.from(new Set(books.map(book => book.genre).filter(genre => genre)));
        setGenres(uniqueGenres);
    }, [books]);

    const handleFilterChange = (e) => {
        const genre = e.target.value;
        setSelectedGenre(genre);
        onFilter(genre); // Chama a função de filtragem passada como prop
    };

    return (
        <div style={{ marginBottom: '20px' }}>
            <select
                value={selectedGenre}
                onChange={handleFilterChange}
                style={{
                    padding: '10px',
                    borderRadius: '5px',
                    border: '1px solid #ccc',
                    width: '200px',
                }}
            >
                <option value="">Todos os Gêneros</option>
                {genres.map((genre, index) => (
                    <option key={index} value={genre}>
                        {genre}
                    </option>
                ))}
            </select>
        </div>
    );
};

export default FilterBar;
