// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import JekLogo from './Logo.js';
import DisplayBooks from './DisplayBooks'; // Importe o componente DisplayBooks

const App = () => {
    const [books, setBooks] = useState([]); // Estado para armazenar a lista de livros
    const [booksUpdated, setBooksUpdated] = useState(false); // Estado para controlar atualizações

    // Função para buscar livros
    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/'); // URL para buscar os livros
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
            setBooks(data); // Atualiza a lista de livros
        } catch (error) {
            console.error("Erro ao buscar livros:", error);
        }
    };

    // UseEffect para buscar livros sempre que booksUpdated mudar
    useEffect(() => {
        fetchBooks();
    }, [booksUpdated]); // Dependência que atualiza quando booksUpdated muda

    // Função para notificar que um livro foi adicionado
    const handleBookAdded = () => {
        setBooksUpdated(prev => !prev); // Alterna o estado para acionar a atualização
    };

    return (
        <div style={{ position: 'relative' }}>
            <JekLogo />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '40px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons onBookAdded={handleBookAdded} /> {/* Passando a função */}
            </div>
            <div style={{ marginTop: '40px', paddingLeft: '200px', paddingRight: '20px' }}>
                <DisplayBooks books={books} /> {/* Passando a lista de livros para o componente */}
            </div>
        </div>
    );
};

export default App;
