// src/App.js
import React from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import JekLogo from './Logo.js';
import DisplayBooks from './DisplayBooks'; // Importe o componente DisplayBooks

const App = () => {
    return (
        <div style={{ position: 'relative' }}>
            <JekLogo />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '40px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons />
            </div>
            <div style={{ marginTop: '40px', paddingLeft: '200px', paddingRight: '20px' }}>
                <DisplayBooks /> {/* Adicione o componente DisplayBooks para mostrar os livros */}
            </div>
        </div>
    );
};

export default App;
