import React, { useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import logo from './assets/jek_logo.png';

const App = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newBook, setNewBook] = useState({
        title: '',
        author: '',
        genre: '',
        published_date: '',
        n_pages: '',
        image: null,
    });

    const handleAdd = () => {
        setIsModalOpen(true);
    };

    const handleConfirmAdd = async () => {
        const formData = new FormData();
        formData.append('title', newBook.title);
        formData.append('author', newBook.author);
        formData.append('genre', newBook.genre);
        formData.append('published_date', newBook.published_date);
        formData.append('n_pages', newBook.n_pages);
        if (newBook.image) {
            formData.append('image', newBook.image);
        }

        try {
            const response = await fetch('http://seu-backend-endereco/api/add', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                alert("Livro adicionado com sucesso!");
                setIsModalOpen(false);
                setNewBook({
                    title: '',
                    author: '',
                    genre: '',
                    published_date: '',
                    n_pages: '',
                    image: null,
                });
            } else {
                alert("Erro ao adicionar livro");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    const handleRemove = async () => {
        // Implementar lógica de remoção conforme necessário
    };

    return (
        <div style={{ position: 'relative' }}>
            <img 
                src={logo} 
                alt="Logo da biblioteca" 
                style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px',
                    width: '150px',
                    height: 'auto'
                }}
            />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '20px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons onAdd={handleAdd} onRemove={handleRemove} />
            </div>

            {isModalOpen && (
                <div style={{
                    position: 'fixed',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'white',
                    padding: '20px',
                    boxShadow: '0px 0px 10px rgba(0,0,0,0.5)',
                    zIndex: 1000
                }}>
                    <h2>Adicionar Novo Livro</h2>
                    <input 
                        type="text" 
                        placeholder="Título" 
                        value={newBook.title}
                        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="text" 
                        placeholder="Autor" 
                        value={newBook.author}
                        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="text" 
                        placeholder="Gênero" 
                        value={newBook.genre}
                        onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="date" 
                        placeholder="Data de Publicação" 
                        value={newBook.published_date}
                        onChange={(e) => setNewBook({ ...newBook, published_date: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="number" 
                        placeholder="Número de Páginas" 
                        value={newBook.n_pages}
                        onChange={(e) => setNewBook({ ...newBook, n_pages: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="file" 
                        onChange={(e) => setNewBook({ ...newBook, image: e.target.files[0] })}
                        style={{ marginBottom: '10px', width: '100%' }}
                    />
                    <button onClick={handleConfirmAdd} style={{ marginRight: '10px' }}>Adicionar</button>
                    <button onClick={() => setIsModalOpen(false)}>Cancelar</button>
                </div>
            )}
            {isModalOpen && <div style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0,0,0,0.3)',
                zIndex: 999
            }}></div>}
        </div>
    );
};

export default App;
