// src/App.js
import React from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import logo from './assets/jek_logo.png';

const App = () => {
    const handleAdd = () => {
        alert("Adicionar ação executada!");
    };

    const handleRemove = () => {
        alert("Remover ação executada!");
    };

    return (
        <div style={{ position: 'relative', padding: '20px' }}>
            <img 
                src={logo} 
                alt="Logo da biblioteca" 
                style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px',
                    maxWidth: '100%', // Responsividade
                    height: 'auto'
                }}
            />
            <div style={{ 
                display: 'flex', 
                flexDirection: 'column', // Alinhar tudo verticalmente em telas menores
                alignItems: 'flex-start', // Alinha ao início
                paddingLeft: '170px',
                paddingTop: '20px',
                width: '100%', // Faz com que o contêiner use 100% da largura disponível
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '20px', fontSize: '2rem' }}>jeKlibrary</h1>
                <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                    <SearchBar />
                    <AddRemoveButtons onAdd={handleAdd} onRemove={handleRemove} />
                </div>
            </div>
        </div>
    );
};

export default App;
