// src/SearchBar.js
import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {  // Recebendo a função onSearch como prop
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = () => {
        onSearch(searchTerm);  // Chama a função de busca com o termo de busca
        setSearchTerm('');  // Limpa o campo de busca após a pesquisa
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', marginRight: '20px' }}>
            <input
                type="text"
                placeholder="Pesquisar livros (pelo título)..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                    backgroundColor:'#00bfbf',
                    width: '680px',
                    padding: '10px',
                    borderRadius: '20px',
                    marginRight: '10px',
                }}
            />
            <button
                onClick={handleSearch}
                style={{
                    padding: '10px 20px',
                    backgroundColor: '#1e67e1',
                    color: 'white',
                    border: 'none',
                    borderRadius: '20px',
                    cursor: 'pointer',
                }}
            >
                Pesquisar
            </button>
        </div>
    );
};

export default SearchBar;
