// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import Logo from './JekLogo';
import DisplayBooks from './DisplayBooks';
import FilterBar from './FilterBar'; // Importe o componente FilterBar

const App = () => {
    const [books, setBooks] = useState([]);
    const [displayedBooks, setDisplayedBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/');
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
            const updatedBooks = data.map(book => ({
                ...book,
                image: book.image ? `http://localhost:8000/media/${book.image}` : null,
            }));
            setBooks(updatedBooks);
            setDisplayedBooks(updatedBooks);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (searchTerm) => {
        const filteredBooks = books.filter(book =>
            book.title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setDisplayedBooks(filteredBooks);
    };

    const handleFilter = (genre) => {
        const filteredBooks = genre
            ? books.filter(book => book.genre === genre)
            : books;
        setDisplayedBooks(filteredBooks);
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>Erro: {error}</div>;

    return (
        <div style={{ display: 'flex', flexDirection: 'column', position: 'relative' }}>
            <div style={{ position: 'fixed', top: '0', left: '0', width: '100%', backgroundColor: '#2c3e50', zIndex: 1000 }}>
                <Logo />
            </div>
            <div style={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'flex-start',
                marginTop: '100px', // Espaço reservado para a logo fixa
                paddingLeft: '270px', // Ajuste para alinhar com a barra de filtro
                paddingTop: '20px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '40px' }}>jeKlibrary</h1>
                <SearchBar onSearch={handleSearch} />
                <AddRemoveButtons onBookAdded={fetchBooks} />
            </div>
            <FilterBar books={books} onFilter={handleFilter} /> {/* Adiciona o FilterBar */}
            <div style={{ marginTop: '20px', paddingLeft: '220px', paddingRight: '20px' }}>
                <DisplayBooks books={displayedBooks} />
            </div>
        </div>
    );
};

export default App;
