import React, { useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons'; // Importe o novo componente
import logo from './assets/jek_logo.png'; // Importe o logo

const App = () => {
    const [isModalOpen, setIsModalOpen] = useState(false); // Estado para controlar a exibição do modal
    const [newBook, setNewBook] = useState({ title: '', author: '' }); // Estado para os dados do novo livro

    const handleAdd = async () => {
        setIsModalOpen(true); // Abre o modal para o usuário inserir os dados
    };

    const handleConfirmAdd = async () => {
        try {
            const response = await fetch('http://seu-backend-endereco/api/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newBook), // Envia os dados do novo livro
            });

            if (response.ok) {
                alert("Livro adicionado com sucesso!");
                setIsModalOpen(false); // Fecha o modal
                setNewBook({ title: '', author: '' }); // Reseta os campos
            } else {
                alert("Erro ao adicionar livro");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    const handleRemove = async () => {
        try {
            const response = await fetch('http://seu-backend-endereco/api/remove', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ itemId: 1 }), // Identificador do item a ser removido
            });

            if (response.ok) {
                alert("Item removido com sucesso!");
            } else {
                alert("Erro ao remover item");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    return (
        <div style={{ position: 'relative' }}>
            <img 
                src={logo} 
                alt="Logo da biblioteca" 
                style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px',
                    width: '150px',
                    height: 'auto'
                }}
            />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '20px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons onAdd={handleAdd} onRemove={handleRemove} />
            </div>

            {/* Modal para adicionar um novo livro */}
            {isModalOpen && (
                <div style={{
                    position: 'fixed',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'white',
                    padding: '20px',
                    boxShadow: '0px 0px 10px rgba(0,0,0,0.5)',
                    zIndex: 1000
                }}>
                    <h2>Adicionar Novo Livro</h2>
                    <input 
                        type="text" 
                        placeholder="Título" 
                        value={newBook.title}
                        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="text" 
                        placeholder="Autor" 
                        value={newBook.author}
                        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <button onClick={handleConfirmAdd} style={{ marginRight: '10px' }}>Adicionar</button>
                    <button onClick={() => setIsModalOpen(false)}>Cancelar</button>
                </div>
            )}
            {isModalOpen && <div style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0,0,0,0.3)',
                zIndex: 999
            }}></div>}
        </div>
    );
};

export default App;
