// src/FilterBar.js
import React, { useState, useEffect } from "react";

const FilterBar = ({ books, onFilter }) => {
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState("");

    // Extrai os gêneros disponíveis a partir da lista de livros
    useEffect(() => {
        const uniqueGenres = Array.from(new Set(books.map(book => book.genre).filter(genre => genre)));
        setGenres(uniqueGenres);
    }, [books]);

    const handleFilterChange = (genre) => {
        setSelectedGenre(genre);
        onFilter(genre); // Chama a função de filtragem passada como prop
    };

    return (
        <div style={{
            width: '200px',
            position: 'fixed',
            top: '150px',
            left: '0',
            height: '100%',
            backgroundColor: '#2c3e50',
            padding: '10px',
            boxSizing: 'border-box',
            color: '#ffffff',
        }}>
            <h3>Filtrar por Gênero</h3>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                <li
                    onClick={() => handleFilterChange("")}
                    style={{
                        cursor: 'pointer',
                        marginBottom: '10px',
                        color: selectedGenre === "" ? '#64b627' : '#ffffff'
                    }}
                >
                    Todos os Gêneros
                </li>
                {genres.map((genre, index) => (
                    <li
                        key={index}
                        onClick={() => handleFilterChange(genre)}
                        style={{
                            cursor: 'pointer',
                            marginBottom: '10px',
                            color: selectedGenre === genre ? '#64b627' : '#ffffff'
                        }}
                    >
                        {genre}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FilterBar;
