import React, { useState } from 'react';

const AddRemoveButtons = ({ fetchBooks }) => {  // Receber a função fetchBooks como prop
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newBook, setNewBook] = useState({
        title: '',
        author: '',
        genre: '',
        published_date: '',
        n_pages: '',
        image: null,
    });

    const handleAdd = () => {
        setIsModalOpen(true);
    };

    const handleConfirmAdd = async () => {
        const formData = new FormData();
        formData.append('title', newBook.title);
        formData.append('author', newBook.author);
        formData.append('genre', newBook.genre);
        formData.append('published_date', newBook.published_date);
        formData.append('n_pages', newBook.n_pages);
        if (newBook.image) {
            formData.append('image', newBook.image);
        }

        try {
            const response = await fetch('http://127.0.0.1:8000/add/', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                alert("Livro adicionado com sucesso!");
                setIsModalOpen(false);
                setNewBook({
                    title: '',
                    author: '',
                    genre: '',
                    published_date: '',
                    n_pages: '',
                    image: null,
                });
                await fetchBooks(); // Chama a função para atualizar a lista de livros
            } else {
                const errorData = await response.json();
                alert("Erro ao adicionar livro: " + JSON.stringify(errorData));
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    const handleRemove = async () => {
        alert("Função de remoção não implementada");
    };

    return (
        <div style={{ display: 'flex', alignItems: 'center', marginLeft: '20px' }}>
            <button 
                onClick={handleAdd} 
                style={{
                    padding: '10px 40px',
                    marginRight: '20px',
                    backgroundColor: '#64b627', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Adicionar livro
            </button>
            <button 
                onClick={handleRemove} 
                style={{
                    padding: '10px 40px',
                    backgroundColor: '#ff4d4d', 
                    color: 'white',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer'
                }}
            >
                Remover livro
            </button>

            {isModalOpen && (
                <div style={{
                    position: 'fixed',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'white',
                    padding: '20px',
                    boxShadow: '0px 0px 10px rgba(0,0,0,0.5)',
                    zIndex: 1000
                }}>
                    <h2>Adicionar Novo Livro</h2>
                    <input 
                        type="text" 
                        placeholder="Título" 
                        value={newBook.title}
                        onChange={(e) => setNewBook({ ...newBook, title: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="text" 
                        placeholder="Autor" 
                        value={newBook.author}
                        onChange={(e) => setNewBook({ ...newBook, author: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="text" 
                        placeholder="Gênero" 
                        value={newBook.genre}
                        onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="number" 
                        placeholder="Data de Publicação" 
                        value={newBook.published_date}
                        onChange={(e) => setNewBook({ ...newBook, published_date: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="number" 
                        placeholder="Número de Páginas" 
                        value={newBook.n_pages}
                        onChange={(e) => setNewBook({ ...newBook, n_pages: e.target.value })}
                        style={{ marginBottom: '10px', width: '100%', padding: '8px' }}
                    />
                    <input 
                        type="file" 
                        onChange={(e) => setNewBook({ ...newBook, image: e.target.files[0] })}
                        style={{ marginBottom: '10px', width: '100%' }}
                    />
                    <button onClick={handleConfirmAdd} style={{ marginRight: '10px' }}>Adicionar</button>
                    <button onClick={() => setIsModalOpen(false)}>Cancelar</button>
                </div>
            )}
            {isModalOpen && <div style={{
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0,0,0,0.3)',
                zIndex: 999
            }}></div>}
        </div>
    );
};

export default AddRemoveButtons;
