import React, { useEffect, useState } from 'react';

const DisplayBooks = () => {
    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/'); // Ajuste a URL conforme necessário
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
            setBooks(data);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>Erro: {error}</div>;

    return (
        <div>
            <h2>Livros Disponíveis</h2>
            <ul>
                {books.map((book, index) => (
                    <li key={index}>
                        <h3>{book.title}</h3>
                        <p>Autor: {book.author}</p>
                        <p>Gênero: {book.genre}</p>
                        <p>Data de Publicação: {book.published_date}</p>
                        <p>Páginas: {book.n_pages}</p>
                        {book.image && <img src={book.image} alt={`Capa do livro ${book.title}`} style={{ width: '100px', height: 'auto' }} />}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DisplayBooks;
