// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons';
import JekLogo from './Logo.js';
import DisplayBooks from './DisplayBooks'; // Importe o componente DisplayBooks

const App = () => {
    const [books, setBooks] = useState([]);
    const [displayedBooks, setDisplayedBooks] = useState([]); // Estado para os livros exibidos
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/');
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
            const updatedBooks = data.map(book => ({
                ...book,
                image: book.image ? `http://localhost:8000/media/${book.image}` : null,
            }));
            setBooks(updatedBooks);
            setDisplayedBooks(updatedBooks); // Inicializa os livros exibidos
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (searchTerm) => {
        const filteredBooks = books.filter(book =>
            book.title.toLowerCase().includes(searchTerm.toLowerCase()) // Filtra livros pelo título
        );
        setDisplayedBooks(filteredBooks); // Atualiza os livros exibidos com os filtrados
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>Erro: {error}</div>;

    return (
        <div style={{ position: 'relative' }}>
            <JekLogo />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '170px', 
                paddingTop: '40px',
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '40px' }}>jeKlibrary</h1>
                <SearchBar onSearch={handleSearch} /> {/* Passa a função de busca para o SearchBar */}
                <AddRemoveButtons onBookAdded={fetchBooks} />
            </div>
            <div style={{ marginTop: '40px', paddingLeft: '260px', paddingRight: '20px' }}>
                <DisplayBooks books={displayedBooks} /> {/* Passa os livros exibidos para o DisplayBooks */}
            </div>
        </div>
    );
};

export default App;
