// src/DisplayBooks.js
import React, { useEffect, useState } from 'react';

const DisplayBooks = () => {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Função para buscar os livros na API
    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/'); // URL da sua API para obter os livros
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
            setBooks(data);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    // useEffect para buscar os livros quando o componente for montado
    useEffect(() => {
        fetchBooks();
    }, []);

    if (loading) {
        return <p>Carregando livros...</p>;
    }

    if (error) {
        return <p>Erro: {error}</p>;
    }

    return (
        <div style={{ padding: '20px' }}>
            <h2>Livros Disponíveis</h2>
            {books.length === 0 ? (
                <p>Nenhum livro encontrado.</p>
            ) : (
                <ul style={{ listStyleType: 'none', paddingLeft: 0 }}>
                    {books.map((book) => (
                        <li key={book.id} style={{ marginBottom: '15px', border: '1px solid #ccc', padding: '10px', borderRadius: '5px' }}>
                            <h3>{book.title}</h3>
                            <p><strong>Autor:</strong> {book.author}</p>
                            <p><strong>Gênero:</strong> {book.genre}</p>
                            <p><strong>Data de Publicação:</strong> {book.published_date}</p>
                            <p><strong>Número de Páginas:</strong> {book.n_pages}</p>
                            {book.image && <img src={book.image} alt={book.title} style={{ width: '100px', height: 'auto' }} />}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default DisplayBooks;
