// src/App.js
import React from 'react';
import './App.css';
import SearchBar from './SearchBar';
import AddRemoveButtons from './AddRemoveButtons'; // Importe o novo componente
import logo from './assets/jek_logo.png'; // Importe o logo

const App = () => {
    const handleAdd = async () => {
        try {
            const response = await fetch('http://seu-backend-endereco/api/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ item: 'Novo item' }), // Dados a serem enviados para o backend
            });

            if (response.ok) {
                alert("Item adicionado com sucesso!");
                // Lógica adicional caso a requisição tenha sido bem-sucedida
            } else {
                alert("Erro ao adicionar item");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    const handleRemove = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8/remove', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ itemId: 1 }), // Identificador do item a ser removido
            });

            if (response.ok) {
                alert("Item removido com sucesso!");
                // Lógica adicional caso a requisição tenha sido bem-sucedida
            } else {
                alert("Erro ao remover item");
            }
        } catch (error) {
            alert("Erro na requisição: " + error.message);
        }
    };

    return (
        <div style={{ position: 'relative' }}>
            <img 
                src={logo} 
                alt="Logo da biblioteca" 
                style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px', // Posiciona o logo no canto superior esquerdo
                    width: '150px', // Tamanho do logo
                    height: 'auto'
                }}
            />
            <div style={{ 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'flex-start', 
                paddingLeft: '200px', // Ajuste para deixar espaço para o logo
                paddingTop: '40px', // Ajuste para espaçar verticalmente
            }}>
                <h1 style={{ color: '#cedae1', marginRight: '20px' }}>jeKlibrary</h1>
                <SearchBar />
                <AddRemoveButtons onAdd={handleAdd} onRemove={handleRemove} /> {/* Adicione o componente */}
            </div>
        </div>
    );
};

export default App;
