import React, { useEffect, useState } from 'react';

const DisplayBooks = ({ refresh }) => { // Adiciona a prop refresh
    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/'); // URL para buscar os livros
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();
    
            // Atualiza a URL da imagem para incluir o prefixo correto
            const updatedBooks = data.map(book => ({
                ...book,
                image: book.image ? `http://localhost:8000/media/${book.image}` : null, // Construa a URL da imagem corretamente
            }));
    
            setBooks(updatedBooks);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };
    
    useEffect(() => {
        fetchBooks(); // Chama a função sempre que o componente é montado ou a prop refresh muda
    }, [refresh]); // Adiciona refresh como dependência

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>Erro: {error}</div>;

    return (
        <div style={{ padding: '20px', backgroundColor: '#2c3e50', borderRadius: '5px' }}> {/* Estilizando o contêiner principal */}
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {books.map((book, index) => (
                    <li key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
                        <div style={{ flexGrow: 1, color: '#dce0df' }}> {/* Cor do texto */}
                            <h3 style={{ color: '#ffffff' }}>{book.title}</h3> {/* Cor do título do livro */}
                            <p>Autor: {book.author}</p>
                            <p>Gênero: {book.genre}</p>
                            <p>Data de Publicação: {book.published_date}</p>
                            <p>Páginas: {book.n_pages}</p>
                        </div>
                        {book.image && (
                            <img
                                src={book.image}
                                alt={`Capa do livro ${book.title}`}
                                style={{ width: '150px', height: 'auto', marginLeft: '10px', marginRight: '20px' }} // Adicionando margem à direita
                            />
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default DisplayBooks;
