// src/FilterBar.js
import React, { useState, useEffect } from "react";

const FilterBar = ({ books, onFilter }) => {
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState("");

    // Extrai os gêneros disponíveis a partir da lista de livros
    useEffect(() => {
        const allGenres = books
            .flatMap(book => book.genre.split(",").map(genre => genre.trim()))
            .filter(genre => genre); // Remove valores vazios
        const uniqueGenres = Array.from(new Set(allGenres)); // Remove duplicados
        setGenres(uniqueGenres);
    }, [books]);

    const handleFilterChange = (genre) => {
        setSelectedGenre(genre);
        if (genre === "") {
            onFilter(() => true); // Mostra todos os livros se nenhum gênero for selecionado
        } else {
            // Filtra os livros que têm o gênero selecionado em sua lista de gêneros
            onFilter(book => {
                const bookGenres = book.genre.split(",").map(g => g.trim().toLowerCase());
                return bookGenres.includes(genre.toLowerCase());
            });
        }
    };

    return (
        <div style={{
            width: '200px',
            marginTop: '20px',
            padding: '20px',
            backgroundColor: '#2c3e50',
            borderRadius: '5px',
            color: '#ffffff',
            boxShadow: '0px 0px 10px rgba(0,0,0,0.3)',
            position: 'absolute',
            left: '20px',
            top: '136px', // Ajuste conforme necessário
        }}>
            <h3>Filtrar por Género</h3>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                <li
                    onClick={() => handleFilterChange("")}
                    style={{
                        cursor: 'pointer',
                        marginBottom: '10px',
                        color: selectedGenre === "" ? '#64b627' : '#ffffff'
                    }}
                >
                    Todos os Géneros
                </li>
                {genres.map((genre, index) => (
                    <li
                        key={index}
                        onClick={() => handleFilterChange(genre)}
                        style={{
                            cursor: 'pointer',
                            marginBottom: '10px',
                            color: selectedGenre === genre ? '#64b627' : '#ffffff'
                        }}
                    >
                        {genre}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FilterBar;
