import React, { useEffect, useState } from 'react';
import DisplayBooks from './DisplayBooks';
import AddRemoveButtons from './AddRemoveButtons';

const App = () => {
    const [books, setBooks] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    const fetchBooks = async () => {
        try {
            const response = await fetch('http://localhost:8000/'); // URL para buscar os livros
            if (!response.ok) {
                throw new Error('Erro ao buscar os livros');
            }
            const data = await response.json();

            // Atualiza a URL da imagem para incluir o prefixo correto
            const updatedBooks = data.map(book => ({
                ...book,
                image: book.image ? `http://localhost:8000/media/${book.image}` : null, // Construa a URL da imagem corretamente
            }));

            setBooks(updatedBooks);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchBooks();
    }, []);

    if (loading) return <div>Carregando...</div>;
    if (error) return <div>Erro: {error}</div>;

    return (
        <div>
            <AddRemoveButtons fetchBooks={fetchBooks} /> {/* Passando a função fetchBooks */}
            <DisplayBooks books={books} />
        </div>
    );
};

export default App;
